mapper.select:
select a.id,a.hot_type_id,a.max_change,a.circulation_market_value,a.full_time,a.continuity_time, a.sort,a.data_date,a.remark,b.remark totalRemark,a.create_time,a.update_time,a.no_deal, a.near_change,a.one_minute_value,a.today_no_deal,a.on_selected,a.change_total, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 1 and data_date < a.data_date) firstTime, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 2 and data_date < a.data_date) secondTime, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 3 and data_date < a.data_date) thirdTime, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 4 and data_date < a.data_date) forthTime, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 5 and data_date < a.data_date) fifthTime, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 6 and data_date < a.data_date) sixthTime, (select count(1) from hot_company_data aa where hot_company_id = a.hot_company_id and continuity_time = 7 and data_date < a.data_date) seventhTime,
(select no_deal from hot_company_data aa where hot_company_id = a.hot_company_id and data_date = #{vo.yesterdayDataDate}) yesterdayNoDeal,
b.name,b.code,b.hot_type_ids,100 - b.percent percent,c.name hotType from hot_company_data a join hot_company b on b.id = a.hot_company_id join hot_type c on c.id = a.hot_type_id
and a.data_date = #{vo.dataDate}
and a.hot_type_id = #{vo.hotTypeId}
and a.hot_company_id = #{vo.hotCompanyId}
and a.continuity_time > -#{vo.continuityTime} and a.hot_type_id != 320
and a.full_time in ('09:25:00','09:30:00')
and a.continuity_time = #{vo.continuityTime}
and a.on_selected = #{vo.onSelected}
order by data_date desc,sort desc,hot_type_id desc,full_time,continuity_time desc,a.id

SELECT DISTINCT data_date FROM hot_company_data
where data_date <= #{q}
ORDER BY data_date desc limit 50
